import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';

const styles = {
  card: {
    width: 280,
  },
  media: {
    height: 300,
  },
};

function CharacterStats(props) {

  const { classes } = props;
  return (
    <Card className={classes.card}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={ props.stats.icon }
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.stats.legendName}
          </Typography>
          <Typography component="p">{props.stats.stats[0].statName + ": " + props.stats.stats[0].value}</Typography>
          <Typography component="p">{props.stats.stats[1].statName + ": " + props.stats.stats[1].value}</Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  );
}

CharacterStats.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(CharacterStats);
