  import React, { Component } from 'react';
  import { AxisLeft, AxisBottom } from '@vx/axis'
  import { scaleLinear, scaleTime } from '@vx/scale'
  import { extent, max } from 'd3-array'
  import { LinePath } from '@vx/shape'
import { curveBasis } from '@vx/curve'


  class Graph extends Component {
    render() {
      const series = [
        [
          { date: new Date(2017, 3, 1), value: 1 },
          { date: new Date(2017, 4, 1), value: 2 },
          { date: new Date(2017, 5, 1), value: 6 }
        ],
        [
          { date: new Date(2017, 3, 1), value: 4},
          { date: new Date(2017, 4, 1), value: 4},
          { date: new Date(2017, 5, 1), value: 0}
        ]
      ]
    const xMax = containerWidth - margin.left - margin.right
    const yMax = (containerWidth * aspectRatio) - margin.top - margin.bottom
    const xScale = scaleTime({
      domain: extent(data, x),
      range: [0, xMax]
    })
    const yScale = scaleLinear({
      domain: [0, max(data, y)],
      range: [yMax, 0]
    })
      const tickLabel = <text fill="grey" opacity="0.20" fontSize={10} dy="0.25em" textAnchor="middle" fontWeight="bold" />
      const x = d => d.date
      const y = d => d.value

      return (
        <div className="Progress">
         <svg width={width} height={height} ref={this.setSvgRef}>
          <AxisLeft
            top={margin.top}
            left={margin.left}
            scale={this.yScale}
            hideTicks
            hideAxisLine
            numTicks={3}
            stroke="#eaf0f6"
            tickLabelComponent={tickLabel}
            tickFormat={this.yScaleFormat}
          />
          <AxisBottom
            top={height - margin.bottom}
            left={margin.left}
            scale={this.xScale}
            hideTicks
            stroke="#eaf0f6"
            tickLabelComponent={tickLabel}
          />
        </svg>
        {series.map((seriesData, i) => (
      <LinePath
        key={i}
        data={seriesData}
        xScale={this.xScale}
        yScale={this.yScale}
        x={x}
        y={y}
        curve={curveBasis}
        stroke={colors[i]}
        strokeLinecap="round"
      />
    ))}
    <p>test</p>
        </div>
      );
    }
  }


  export default Graph;
